import React from 'react'
import { Container } from 'react-bootstrap';
import Row from 'react-bootstrap/Row';
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";



export const ItemDetail = ({ id, title, stock, price, thumbnail }) => {
  return (

<Container>
<Row>
<Col xs={12} md={4} sm={6} lg={12}>
      <Card key={id}>
            <Card.Img variant="top" src={thumbnail} />
            <Card.Body>
                <Card.Title><Link to={`/item/${id}`} className="tituloProducto">{title}</Link></Card.Title>
                <Card.Text className="stock">Stock: {stock} u.</Card.Text>
                <Card.Text className="precio"> Precio: {price}</Card.Text>
                {/* <Button variant="primary" className="botonCarrito">Agregar al carrito</Button> */}
            </Card.Body>
      </Card>
    </Col>
            </Row>
            </Container>
  )
}

export default ItemDetail
