import React from 'react'
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";



export const ItemDetail = ({ item }) => {
  return (
    <Col xs={12} md={4} sm={6} lg={9}>
      <Card key={item.id}>
            <Card.Img variant="top" src={item.images} />
            <Card.Body>
                <Card.Title><Link to={`/item/${item.id}`} className="tituloProducto">{item.name}</Link></Card.Title>
                <Card.Text className="stock">Stock: {item.stock} u.</Card.Text>
                <Card.Text className="precio"> Precio: {item.price}</Card.Text>
                {/* <Button variant="primary" className="botonCarrito">Agregar al carrito</Button> */}
            </Card.Body>
      </Card>
    </Col>
  )
}

export default ItemDetail
